        // Thêm chú thích mức độ nguy hiểm trên bản đồ
        this.addLegend();
      },
      // Hàm vẽ vùng nguy hiểm dựa vào mức độ
      drawCircleBasedOnLevel(location) {
        const color = this.getColorByLevel(location.muc_do); // Lấy màu sắc theo mức độ
        const radius = this.getRadiusByLevel(location.muc_do); // Lấy bán kính dựa vào mức độ

        // Vẽ vùng tròn trên bản đồ
        this.mapInstance.veVungTron(location.toa_do.lat, location.toa_do.lng, radius, color);
      },
      // Xác định màu sắc dựa vào mức độ
      getColorByLevel(muc_do) {
        if (muc_do <= 2) {
          return "#FF0000"; // Mức độ nguy hiểm cao: Đỏ
        } else if (muc_do <= 6) {
          return "#FFFF00"; // Mức độ nguy hiểm trung bình: Vàng
        } else {
          return "#00FF00"; // Mức độ an toàn: Xanh lá
        }
      },
      // Xác định bán kính dựa vào mức độ
      getRadiusByLevel(muc_do) {
        if (muc_do <= 3) {
          return 1500; // 1.5 km
        } else if (muc_do <= 6) {
          return 1000; // 1 km
        } else {
          return 500; // 500 m
        }
      },
      // Thêm chú thích mức độ nguy hiểm
      addLegend() {
        const legendHtml = `
					<div style="background: white; padding: 10px; border-radius: 8px; box-shadow: 0 2px 6px rgba(0,0,0,0.3);">
						<h4>Chú thích mức độ nguy hiểm</h4>
						<div><span style="background: #FF0000; width: 15px; height: 15px; display: inline-block; margin-right: 5px;"></span> Nguy hiểm cao</div>
						<div><span style="background: #FFFF00; width: 15px; height: 15px; display: inline-block; margin-right: 5px;"></span> Nguy hiểm trung bình</div>
						<div><span style="background: #00FF00; width: 15px; height: 15px; display: inline-block; margin-right: 5px;"></span> An toàn</div>
					</div>
				`;
        this.mapInstance.addControl(legendHtml, {
          position: "bottomright"
        });
      }
    }
  });
});

