async () => {
  const token = appsmith.store.tokenAdmin;
  const decoded = await new Promise((resolve, reject) => {
    jsonwebtoken.verify(token, 'secret', (err, decoded) => {
      if (err) {
        reject(err);
      } else {
        resolve(decoded);
      }
    });
  });
  console.log(decoded);
  const [check_acp] = await FindAdminBuyToken.run(decoded);
  if (check_acp) {
    return true;
  } else {
    return false;
  }
}

