appsmith.onReady(() => {
	new Vue({
		el: "#app",
		data() {
			return {
				mapInstance: null,
				locationsList: appsmith.model.map, // Danh sách các địa điểm nguy hiểm
				position: { lat: 15.115466, lng: 108.804808 }, // Vị trí hiện tại
			};
		},
		async mounted() {
			this.initMap();
			this.hienThiDanhSachDiaChi();
		},
		methods: {
			// Hàm khởi tạo bản đồ
			initMap() {
				this.mapInstance = new DTUAppsmithMap("map", {
					center: [this.position.lat, this.position.lng],
					zoom: 10,
				});

				// Hiển thị vị trí hiện tại trên bản đồ
				this.mapInstance.capNhatViTriHienTai(
					this.position.lat,
					this.position.lng,
					"Vị trí hiện tại",
					"Bạn đang ở đây"
				);
			},

			// Hàm hiển thị danh sách địa chỉ nguy hiểm
			hienThiDanhSachDiaChi() {
				this.locationsList.forEach((location) => {
					// Tính khoảng cách từ vị trí hiện tại đến địa điểm
					const khoang_cach = this.mapInstance.tinhKhoangCachDenDiem(
						this.position.lat,
						this.position.lng,
						location.toa_do.lat,
						location.toa_do.lng
					);
					location.content = `Cách ${khoang_cach.toFixed(2)} km so với vị trí hiện tại`;

					// Vẽ vùng nguy hiểm dựa vào mức độ
					this.drawCircleBasedOnLevel(location);

					// Hiển thị điểm đánh dấu trên bản đồ với nội dung
					this.mapInstance.taoDiaDiem(
						location.toa_do.lat,
						location.toa_do.lng,
						location.ten_vung || "Khu vực nguy hiểm",
						location.loai_nguy_hiem,
						"fas fa-exclamation-triangle",
						this.getColorByLevel(location.muc_do)
					);
				});

				// Thêm chú thích mức độ nguy hiểm trên bản đồ
				this.addLegend();
			},

			// Hàm vẽ vùng nguy hiểm dựa vào mức độ
			drawCircleBasedOnLevel(location) {
				const color = this.getColorByLevel(location.muc_do); // Lấy màu sắc theo mức độ
				const radius = this.getRadiusByLevel(location.muc_do); // Lấy bán kính dựa vào mức độ

				// Vẽ vùng tròn trên bản đồ
				this.mapInstance.veVungTron(
					location.toa_do.lat,
					location.toa_do.lng,
					radius,
					color
				);
			},

			// Xác định màu sắc dựa vào mức độ
			getColorByLevel(muc_do) {
				if (muc_do <= 2) {
					return "#FF0000"; // Mức độ nguy hiểm cao: Đỏ
				} else if (muc_do <= 6) {
					return "#FFFF00"; // Mức độ nguy hiểm trung bình: Vàng
				} else {
					return "#00FF00"; // Mức độ an toàn: Xanh lá
				}
			},

			// Xác định bán kính dựa vào mức độ
			getRadiusByLevel(muc_do) {
				if (muc_do <= 3) {
					return 1500; // 1.5 km
				} else if (muc_do <= 6) {
					return 1000; // 1 km
				} else {
					return 500; // 500 m
				}
			},

			// Thêm chú thích mức độ nguy hiểm
			addLegend() {
				const legendHtml = `
					<div style="background: white; padding: 10px; border-radius: 8px; box-shadow: 0 2px 6px rgba(0,0,0,0.3);">
						<h4>Chú thích mức độ nguy hiểm</h4>
						<div><span style="background: #FF0000; width: 15px; height: 15px; display: inline-block; margin-right: 5px;"></span> Nguy hiểm cao</div>
						<div><span style="background: #FFFF00; width: 15px; height: 15px; display: inline-block; margin-right: 5px;"></span> Nguy hiểm trung bình</div>
						<div><span style="background: #00FF00; width: 15px; height: 15px; display: inline-block; margin-right: 5px;"></span> An toàn</div>
					</div>
				`;
				this.mapInstance.addControl(legendHtml, { position: "bottomright" });
			},
		},
	});
});

