import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import ReactDOM from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';

function CustomWidget() {
    // Initialize state with mock data
    const [data, setData] = React.useState(appsmith.model.data || { message: "Cảnh báo thiên tai: Lũ lụt và bão đang ảnh hưởng đến khu vực. Hãy nhanh chóng sơ tán nếu cần. Liên hệ khẩn cấp: 0708585120" });

    // Update state when model data changes
    React.useEffect(() => {
        appsmith.onModelChange((model, prevModel) => {
            if (JSON.stringify(model.data) !== JSON.stringify(prevModel?.data)) {
                setData(model.data);
            }
        });
    }, []);

    // Handle user actions
    const handleAction = React.useCallback((eventData) => {
        appsmith.triggerEvent("onAction", eventData);
    }, [data]);

    return (
        <div className="alert">
            <marquee className="marquee">
                <i className="fas fa-radiation" style={{ color: '#ff0000', marginRight: '8px' }}></i>
                {data.message}
            </marquee>
        </div>
    );
}

// Render the component to Appsmith
appsmith.onReady(() => {
    ReactDOM.render(<CustomWidget />, document.getElementById("root"));
});

