appsmith.onReady(() => {
  new Vue({
    el: "#app",
    data() {
      return {
        mapInstance: null,
        position: appsmith.model.location,
        // Vị trí hiện tại
        locationsList: appsmith.model.map // Danh sách các địa điểm
      };
    },
    async mounted() {
      this.initMap();
      this.hienThiDanhSachDiaChi();
    },
    methods: {
      // Hàm khởi tạo bản đồ
      initMap() {
        this.mapInstance = new DTUAppsmithMap("map", {
          center: [this.position.lat, this.position.lng],
          zoom: 16
        });
        this.mapInstance.capNhatViTriHienTai(this.position.lat, this.position.lng);

        // Duyệt qua từng địa điểm và khoanh vùng với màu sắc dựa vào "muc_do"
        this.locationsList.forEach((value, index) => {
          this.mapInstance.taoDiaDiem(value.toa_do_x,
          // latitude
          value.toa_do_y,
          // longitude
          `${value.ten_nha_tai_tro} cung cấp ${value.loai_nguon_luc}`,
          // title
          value.dia_chi,
          // title
          value.loai_icon,
          // title
          "#003C71",
          // icon color
          {
            // options
            marker: {},
            popup: {}
          });
        });
      },
      drawCircleBasedOnLevel(location) {
        let radius = 1000;
        this.mapInstance.veVungTron(location.toa_do_x, location.toa_do_y, radius, "#00FF00");
      },
      reset() {
        // Reset currentIndex và kích hoạt sự kiện "onReset"
        this.currentIndex = 0;
        appsmith.triggerEvent("onReset");
      },
      showPosition(position) {
        console.log(position);
      }
    }
  });
});

