  font-family: "Roboto", sans-serif;
  background: #f9f9f9;
  color: #333;
}

.popup-content {
  min-width: 250px;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 6px 20px rgba(0, 0, 0, 0.15);
}

.popup-title {
  background: linear-gradient(145deg, #0056b3, #003c71);
  color: white;
  margin: 0;
  padding: 12px 20px;
  font-size: 18px;
  font-weight: bold;
  border-radius: 8px 8px 0 0;
}

.popup-body {
  padding: 15px;
  color: #555;
  font-size: 15px;
  line-height: 1.6;
  background-color: #fff;
}

.popup-footer {
  padding: 10px 20px;
  background: #f7f7f7;
  color: #777;
