}

#sendAddress {
  display: flex; /* Căn giữa icon và text */
  align-items: center;
  justify-content: center;
  gap: 8px; /* Khoảng cách giữa icon và text */
  margin: 10px auto;
  padding: 10px 14px; /* Kích thước nhỏ gọn */
  font-size: 14px;
  background: linear-gradient(145deg, #007bff, #0056b3);
  color: white;
  border: none;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.15);
  transition: background-color 0.3s ease, transform 0.2s ease;
  cursor: pointer;
  width: auto; /* Linh hoạt kích thước */
  max-width: 200px;
}

#sendAddress i {
  font-size: 16px; /* Kích thước icon */
}

#sendAddress:hover {
  background: linear-gradient(145deg, #0056b3, #00408a);
  transform: translateY(-2px);
  box-shadow: 0 6px 12px rgba(0, 0, 0, 0.2);
}

#sendAddress:active {
  background: linear-gradient(145deg, #00408a, #003367);
  transform: translateY(0);
  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1);
}

