    padding: 12px 16px; /* Tăng padding để dễ bấm hơn */
    font-size: 14px;
    gap: 10px;
  }
  #sendAddress i {
    font-size: 18px; /* Icon lớn hơn trên màn hình nhỏ */
  }
}
/* Responsive cho thiết bị rất nhỏ hơn 576px */
@media (max-width: 576px) {
  #sendAddress {
    font-size: 13px; /* Cỡ chữ nhỏ hơn */
    padding: 10px 12px;
  }
  #sendAddress i {
    font-size: 16px; /* Giữ icon vừa phải */
  }
}

srcDoc.js:
appsmith.onReady(() => {
  function createVueInstance() {
    new Vue({
      el: "#app",
      data() {
        return {
          mapInstance: null,
          currentPosition: {
            lat: 16.0464319,
            lng: 108.2326082
          },
          // Default current position
          searchPosition: null,
          address: appsmith.model.address
        };
      },
      async mounted() {
        this.initMap();
        this.mapInstance.capNhatViTriHienTai(this.currentPosition.lat, this.currentPosition.lng);
        if (this.address) {
          await this.search(this.address);
        }
        document.getElementById('sendAddress').addEventListener('click', this.sendAddress);
      },
      methods: {
        initMap() {
          this.mapInstance = new DTUAppsmithMap("map", {
            center: [this.currentPosition.lat, this.currentPosition.lng],
            zoom: 15
          });
        },
        async search(address) {
          try {
            const coordinates = await this.mapInstance.timKiemDiaChi(address);
            this.searchPosition = coordinates;
            this.mapInstance.taoDiaDiem(coordinates.lat, coordinates.lng, "Vị trí tìm kiếm", this.address, "fa-solid fa-location-dot");
            this.mapInstance.setView([coordinates.lat, coordinates.lng], 16);

            // Lưu coordinates vào Appsmith Store để sử dụng ở nơi khác
            appsmith.triggerEvent('changeLocation', {
              address: this.searchPosition
            });

            // Trigger event with coordinates
            // appsmith.triggerEvent('changeLocation', { location: coordinates || this.currentPosition });
          } catch (error) {
            console.log(error);
          }
        },
        sendAddress() {
          // Trigger event with address
          appsmith.triggerEvent('changeLocation', {
            address: this.searchPosition || this.currentPosition
          });
        }
      }
      // watch: {
      // address(newAddress) {
      // if (newAddress) {
      // this.search(newAddress);
      // }
      // }
      // }
    });
  }
  createVueInstance(); // Tạo instance khi Appsmith sẵn sàng

  appsmith.onModelChange(() => {
    createVueInstance(); // Tạo lại instance khi model thay đổi
  });
});

