  var location = await appsmith.geolocation.getCurrentPosition();
  return {
    'lat': location.coords.latitude,
    'lng': location.coords.longitude,
    "title": "Vị trí của bạn",
    "location": "Vị trí của bạn đang đứng"
  };
}

