import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import ReactDOM from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';

function MarqueeWidget() {
	const [data, setData] = React.useState(appsmith.model.dataTBs || []);

	React.useEffect(() => {
		appsmith.onModelChange((model, prevModel) => {
			if (JSON.stringify(model.dataTBs) !== JSON.stringify(prevModel?.dataTBs)) {
				setData(model.dataTBs);
			}
		});
	}, []);

	const marqueeText = data.map(item => (
		<span key={item.id}>
			{<span className="icon-danger">⚠️</span>}
			{item.tieu_de}: {item.noi_dung}
		</span>
	)).reduce((prev, curr) => [prev, ' | ', curr]);

	return (
		<div id="marquee">
			<div className="marquee-content">{marqueeText}</div>
		</div>
	);
}

appsmith.onReady(() => {
	ReactDOM.render(<MarqueeWidget />, document.getElementById('root'));
});

